import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './Chat.css';
import {Link} from "react-router-dom";

const socket = io('http://localhost:3000', {
    transports: ['websocket'], // 웹소켓 사용
    withCredentials: true, // CORS 허용
});

function Chat() {
    const [rooms, setRooms] = useState(['Room1', 'Room2', 'Room3']);
    const [currentRoom, setCurrentRoom] = useState('Room1');
    const [messages, setMessages] = useState([]);
    const [message, setMessage] = useState('');
    const [searchTerm, setSearchTerm] = useState('');
    // const [userName, setUserName] = useState('구준휘'); // 사용자 이름 상태 추가
    const [userName, setUserName] = useState(localStorage.getItem('username')); // Retrieve username from local storage

    useEffect(() => {
        socket.emit('joinRoom', currentRoom);
        socket.on('receiveMessage', (data) => {
            setMessages(prevMessages => {
                const updatedMessages = { ...prevMessages };
                if (!updatedMessages[data.room]) {
                    updatedMessages[data.room] = []; // Initialize messages for the room if not present
                }
                updatedMessages[data.room].push(data); // Add message to the corresponding room
                return updatedMessages;
            });
        });

        return () => {
            socket.off('receiveMessage');
        };
    }, [currentRoom, messages]);

    const sendMessage = () => {
        socket.emit('sendMessage', { room: currentRoom, message, userName }); // userName 추가
        setMessage('');
        setMessages([...messages, { message, userName }]); // 채팅 내용과 사용자 이름 추가
    };

    const filteredRooms = rooms.filter(room => room.toLowerCase().includes(searchTerm.toLowerCase()));

    return (
        <div className="chat-container">
            <div className="sidebar">
                <div className="top-bar">
                    <input
                        type="text"
                        placeholder="Search rooms..."
                        value={searchTerm}
                        onChange={(e) => setSearchTerm(e.target.value)}
                        className="search-input"
                    />
                </div>
                <div className="room-list">
                    {filteredRooms.map((room) => (
                        <div
                            key={room}
                            className={`room ${room === currentRoom ? 'active' : ''}`}
                            onClick={() => setCurrentRoom(room)}
                        >
                            {room}
                        </div>
                    ))}
                </div>
            </div>
            <div className="main-chat">
                <div className="top-bar">
                    <h2 className="room-name">{currentRoom}</h2>
                    <div className="user-controls">
                        <button className="user-button">{userName}님</button>
                        <button className="logout-button"><Link to="/" style={{ textDecoration: 'none', color: '#fff' }}>로그아웃</Link></button>
                    </div>
                </div>
                <div className="chat-window">
                    <div className="messages">
                        {(messages[currentRoom] || []).map((msg, index) => (
                            <div key={index} className="message">
                                <strong>{msg.userName}: </strong>{msg.message} {/* 사용자 이름 추가 */}
                            </div>
                        ))}
                    </div>
                    <div className="input-box">
                        <input
                            type="text"
                            value={message}
                            onChange={(e) => setMessage(e.target.value)}
                            placeholder="Type a message..."
                        />
                        <button onClick={sendMessage}>전송</button>
                    </div>
                </div>
            </div>
        </div>
    );
}

export default Chat;
