import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, Navigate } from 'react-router-dom'; // 필요한 컴포넌트를 import합니다.
import Register from './Register'; // Register 컴포넌트를 import합니다.
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            // 로그인 로직을 처리합니다.
        } catch (err) {
            setError('네트워크 오류');
        }
    };

    return (
        <Router> {/* Login 컴포넌트를 BrowserRouter로 감싸줍니다. */}
            <Routes>
                <Route path="/register" element={<Register />} /> {/* Register 컴포넌트를 라우팅합니다. */}
                <Route path="/" element={ /* '/' 경로로 들어왔을 때 로그인 폼을 렌더링합니다. */
                    <div className="login-container">
                        <h2>챗앱 로그인</h2>
                        <div className="form-group">
                            <label htmlFor="email">아이디:</label>
                            <input
                                type="email"
                                id="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">비밀번호:</label>
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        {error && <p className="error-message">{error}</p>}
                        <div className="login-button-container">
                            <button type="button" className="login-button"><Navigate to="/register" replace />회원가입</button>
                            <button type="submit" className="login-button" onClick={handleSubmit}>로그인</button>
                        </div>
                    </div>
                } />
            </Routes>
        </Router>
    );
};

export default Login;
