import React, { useState } from 'react';
import { BrowserRouter as Router, Routes, Route, useHistory } from 'react-router-dom'; // Importing necessary components from react-router-dom
import Register from './Register'; // Importing Register component
import './Login.css';

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const history = useHistory();

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:3000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                alert(data.message);
            } else {
                setError(data.error);
            }
        } catch (err) {
            setError('네트워크 오류');
        }
    };

    const handleSignUp = () => {
        history.push('/register'); // Redirecting to '/register' route
    };

    return (
        <Router>
            <Routes>
                <Route path="/register" element={<Register />} /> {/* Route for Register component */}
                <Route path="/" element={
                    <div className="login-container">
                        <h2>챗앱 로그인</h2>
                        <div className="form-group">
                            <label htmlFor="email">아이디:</label>
                            <input
                                type="email"
                                id="email"
                                value={email}
                                onChange={(e) => setEmail(e.target.value)}
                                required
                            />
                        </div>
                        <div className="form-group">
                            <label htmlFor="password">비밀번호:</label>
                            <input
                                type="password"
                                id="password"
                                value={password}
                                onChange={(e) => setPassword(e.target.value)}
                                required
                            />
                        </div>
                        {error && <p className="error-message">{error}</p>}
                        <div className="login-button-container">
                            <button type="button" className="login-button" onClick={handleSignUp}>회원가입</button>
                            <button type="submit" className="login-button" onClick={handleSubmit}>로그인</button>
                        </div>
                    </div>
                } />
            </Routes>
        </Router>
    );
};

export default Login;
