import React, { useState } from 'react';
import './Login.css';
import {Route, BrowserRouter as Router, Routes, useNavigate} from "react-router-dom";

function Login() {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate();

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:3000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                alert(data.message);
                // Redirect or perform other actions upon successful login
            } else {
                alert(data.error);
            }
        } catch (err) {
            alert('Network error');
        }
    };

    const handleRegisterClick = () => {
        navigate('/register');
    };

    return (
        <Router>
        <div className="login-container">
            <h2>챗앱 로그인</h2>
            <div className="form-group">
                <label htmlFor="email">아이디:</label>
                <input
                    type="email"
                    id="email"
                    value={email}
                    onChange={(e) => setEmail(e.target.value)}
                    required
                />
            </div>
            <div className="form-group">
                <label htmlFor="password">비밀번호:</label>
                <input
                    type="password"
                    id="password"
                    value={password}
                    onChange={(e) => setPassword(e.target.value)}
                    required
                />
            </div>
            <div className="login-button-container">
                <button className="login-button" onClick={handleRegisterClick}>회원가입</button>
                <button type="submit" className="login-button" onClick={handleSubmit}>로그인</button>
            </div>
        </div>
            <Routes>
                <Route path="/register" element={<Register />} />
            </Routes>
        </Router>
    );
};

function Register() {
    // State variables to store user input
    const [id, setId] = useState('');
    const [name, setName] = useState('');
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [phoneNumber, setPhoneNumber] = useState('');

    // Function to handle form submission
    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            // Send a POST request to the server to insert a new record into the Customer table
            const response = await fetch('http://localhost:3000/register', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ id, name, email, password, phoneNumber }),
            });

            // Handle response from the server
            if (response.ok) {
                // Registration successful
                const data = await response.json();
                console.log(data.message); // Optional: Log success message
            } else {
                // Registration failed
                const errorData = await response.json();
                console.error(errorData.error); // Optional: Log error message
            }
        } catch (error) {
            console.error('Error:', error); // Log any network errors
        }
    };

    return (
        <div>
            <h2>User Registration</h2>
            <form onSubmit={handleSubmit}>
                <div>
                    <label htmlFor="id">ID:</label>
                    <input type="text" id="id" value={id} onChange={(e) => setId(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="name">Name:</label>
                    <input type="text" id="name" value={name} onChange={(e) => setName(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="email">Email:</label>
                    <input type="email" id="email" value={email} onChange={(e) => setEmail(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="password">Password:</label>
                    <input type="password" id="password" value={password} onChange={(e) => setPassword(e.target.value)} required />
                </div>
                <div>
                    <label htmlFor="phoneNumber">Phone Number:</label>
                    <input type="tel" id="phoneNumber" value={phoneNumber} onChange={(e) => setPhoneNumber(e.target.value)} required />
                </div>
                <button type="submit">Register</button>
            </form>
        </div>
    );
}

export default Login;
