import React, { useState } from 'react';
import { BrowserRouter as Router, useNavigate } from 'react-router-dom'; // Import Router and useNavigate
import './Login.css'; // 스타일링을 위한 CSS 임포트

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');
    const navigate = useNavigate(); // Access the navigate object

    const handleSignUp = () => {
        // Redirect to registration page when "회원가입" button is clicked
        navigate('/register');
    };

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:3000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                // 성공적인 로그인 처리
                alert(data.message);
                // 대시보드 또는 다른 페이지로 리디렉션
            } else {
                // 에러 처리
                setError(data.error);
            }
        } catch (err) {
            setError('네트워크 오류');
        }
    };

    return (
        <Router>
        <div className="login-container">
            <h2>챗앱 로그인</h2>
                <div className="form-group">
                    <label htmlFor="email">아이디:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">비밀번호:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                {error && <p className="error-message">{error}</p>}
                <div className="login-button-container">
                    <button type="button" className="login-button" onClick={handleSignUp}>회원가입</button>
                    <button type="submit" className="login-button" onClick={handleSubmit}>로그인</button>
                </div>
        </div>
        </Router>
    );
};

export default Login;
