import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './Profile.css';
import { Link } from 'react-router-dom';

const socket = io('http://localhost:3000', {
    transports: ['websocket'],
    withCredentials: true,
});

function Profile() {
    const [userInfo, setUserInfo] = useState({});
    const userName = localStorage.getItem('username');

    useEffect(() => {
        const fetchUserInfo = async () => {
            try {
                const response = await fetch(`http://localhost:3000/fetchUserInfo`);
                const data = await response.json();
                setUserInfo(data.userInfo);
            } catch (error) {
                console.error('Failed to fetch user info:', error);
            }
        };
        fetchUserInfo();
    }, [userName]);

    return (
        <div className="profile-container">
            <div className="top-bar">
                <h2>{userName}님 회원정보</h2>
                <div className="user-controls">
                    <button className="chat-button">
                        <Link to="/chat" style={{ textDecoration: 'none', color: '#fff' }}>채팅 페이지로 이동</Link>
                    </button>
                    <button className="logout-button">
                        <Link to="/" style={{ textDecoration: 'none', color: '#fff' }}>로그아웃</Link>
                    </button>
                </div>
            </div>
            <div className="user-info">
                <p><strong>이름:</strong> {userInfo.name}</p>
                <p><strong>이메일:</strong> {userInfo.email}</p>
                <p><strong>비밀번호:</strong> {userInfo.password}</p>
                <p><strong>전화번호:</strong> {userInfo.phone}</p>
            </div>
        </div>
    );
}

export default Profile;
