import React, { useState } from 'react';
import { Link, BrowserRouter as Router } from 'react-router-dom'; // BrowserRouter 추가
import './Login.css'; // 스타일링 import

const Login = () => {
    const [email, setEmail] = useState('');
    const [password, setPassword] = useState('');
    const [error, setError] = useState('');

    const handleSubmit = async (event) => {
        event.preventDefault();

        try {
            const response = await fetch('http://localhost:3000/login', {
                method: 'POST',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify({ email, password }),
            });

            const data = await response.json();

            if (response.ok) {
                alert(data.message);
                // 로그인이 성공하면 대시보드 또는 다른 페이지로 이동
            } else {
                setError(data.error);
            }
        } catch (err) {
            setError('네트워크 오류');
        }
    };

    return (
        <Router> {/* Router로 감싸기 */}
            <div className="login-container">
                <h2>챗앱 로그인</h2>
                <div className="form-group">
                    <label htmlFor="email">아이디:</label>
                    <input
                        type="email"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        required
                    />
                </div>
                <div className="form-group">
                    <label htmlFor="password">비밀번호:</label>
                    <input
                        type="password"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        required
                    />
                </div>
                {error && <p className="error-message">{error}</p>}
                <div className="login-button-container">
                    {/*<Link to="/register">*/}
                        <button type="register" className="login-button">회원가입</button>
                    {/*</Link>*/}
                    <button type="submit" className="login-button" onClick={handleSubmit}>로그인</button>
                </div>
            </div>
        </Router>
    );
};

export default Login;
