import React, { useState, useEffect } from 'react';
import io from 'socket.io-client';
import './Profile.css'; // 스타일 파일 import
import { Link } from "react-router-dom";

const socket = io('http://localhost:3000', {
    transports: ['websocket'], // 웹소켓 사용
    withCredentials: true, // CORS 허용
});

function Profile() {
    const [userInfo, setUserInfo] = useState({}); // 사용자 정보를 저장할 상태 변수

    useEffect(() => {
        // localStorage에서 username을 가져와서 해당 사용자의 정보를 가져오는 함수
        const fetchUserInfo = async () => {
            try {
                const response = await fetch(`http://localhost:3000/fetchUserInfo/${localStorage.getItem('username')}`);
                const data = await response.json();
                setUserInfo(data.user); // 사용자 정보 설정
            } catch (error) {
                console.error('Failed to fetch user info:', error);
            }
        };

        fetchUserInfo(); // 함수 호출
    }, []);

    return (
        <div className="profile-container">
            <h2>회원정보</h2> {/* 회원정보 텍스트 표시 */}
            <div className="user-info">
                <p><strong>이름:</strong> {userInfo.name}</p> {/* 이름 필드 값 표시 */}
                <p><strong>이메일:</strong> {userInfo.email}</p> {/* Email 필드 값 표시 */}
                <p><strong>비밀번호:</strong> {userInfo.password}</p> {/* Email 필드 값 표시 */}
                <p><strong>전화번호:</strong> {userInfo.phoneNumber}</p> {/* 전화번호 필드 값 표시 */}
            </div>
            <div className="chat-button-container">
                <button className="chat-button"><Link to="/chat" style={{ textDecoration: 'none', color: '#fff' }}>채팅 페이지로 이동</Link></button> {/* 채팅 페이지로 이동하는 버튼 */}
            </div>
        </div>
    );
}

export default Profile;
